import { Finger, FingerCurl, FingerDirection } from '../FingerDescription';
import GestureDescription from '../GestureDescription';


// describe rockSign gesture ðŸ‘ˆ
const pointLeftDescription = new GestureDescription('PointLeft');


// thumb:
pointLeftDescription.addDirection(Finger.Thumb, FingerDirection.NoCurl, 1.0);
pointLeftDescription.addDirection(Finger.Thumb, FingerDirection.NoCurl, 0.9);
pointLeftDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalLeft, 1.0);
pointLeftDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalLeft, 1.0);

// index:
pointLeftDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 1.0);
pointLeftDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 0.9);
pointLeftDescription.addCurl(Finger.Index, FingerCurl.HalfCurl, 1.0);
pointLeftDescription.addCurl(Finger.Index, FingerCurl.HalfCurl, 0.9);
pointLeftDescription.addDirection(Finger.Index, FingerDirection.HorizontalLeft, 1.0);
pointLeftDescription.addDirection(Finger.Index, FingerDirection.HorizontalLeft, 0.9);

// middle:
pointLeftDescription.addCurl(Finger.Middle, FingerCurl.FullCurl, 1.0);
pointLeftDescription.addCurl(Finger.Middle, FingerCurl.FullCurl, 0.9);
pointLeftDescription.addDirection(Finger.Middle, FingerDirection.HorizontalLeft, 1.0);
pointLeftDescription.addDirection(Finger.Middle, FingerDirection.HorizontalLeft, 0.9);

// ring:
pointLeftDescription.addCurl(Finger.Ring, FingerCurl.FullCurl, 1.0);
pointLeftDescription.addCurl(Finger.Ring, FingerCurl.FullCurl, 0.9);
pointLeftDescription.addDirection(Finger.Ring, FingerDirection.HorizontalLeft, 1.0);
pointLeftDescription.addDirection(Finger.Ring, FingerDirection.HorizontalLeft, 0.9);

// pinky:
pointLeftDescription.addCurl(Finger.Pinky, FingerCurl.FullCurl, 1.0);
pointLeftDescription.addCurl(Finger.Pinky, FingerCurl.FullCurl, 0.9);
pointLeftDescription.addDirection(Finger.Pinky, FingerDirection.HorizontalLeft, 1.0);
pointLeftDescription.addDirection(Finger.Pinky, FingerDirection.HorizontalLeft, 0.9);

export default pointLeftDescription;
