import { Finger, FingerCurl, FingerDirection } from '../FingerDescription';
import GestureDescription from '../GestureDescription';


// describe rockSign gesture ðŸ‘‰
const pointRightDescription = new GestureDescription('PointRight');


// thumb:
pointRightDescription.addDirection(Finger.Thumb, FingerDirection.NoCurl, 1.0);
pointRightDescription.addDirection(Finger.Thumb, FingerDirection.NoCurl, 0.9);
pointRightDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalRight, 1.0);
pointRightDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalRight, 1.0);

// index:
pointRightDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 1.0);
pointRightDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 0.9);
pointRightDescription.addCurl(Finger.Index, FingerCurl.HalfCurl, 1.0);
pointRightDescription.addCurl(Finger.Index, FingerCurl.HalfCurl, 0.9);
pointRightDescription.addDirection(Finger.Index, FingerDirection.HorizontalRight, 1.0);
pointRightDescription.addDirection(Finger.Index, FingerDirection.HorizontalRight, 0.9);

// middle:
pointRightDescription.addCurl(Finger.Middle, FingerCurl.FullCurl, 1.0);
pointRightDescription.addCurl(Finger.Middle, FingerCurl.FullCurl, 0.9);
pointRightDescription.addDirection(Finger.Middle, FingerDirection.HorizontalRight, 1.0);
pointRightDescription.addDirection(Finger.Middle, FingerDirection.HorizontalRight, 0.9);

// ring:
pointRightDescription.addCurl(Finger.Ring, FingerCurl.FullCurl, 1.0);
pointRightDescription.addCurl(Finger.Ring, FingerCurl.FullCurl, 0.9);
pointRightDescription.addDirection(Finger.Ring, FingerDirection.HorizontalRight, 1.0);
pointRightDescription.addDirection(Finger.Ring, FingerDirection.HorizontalRight, 0.9);

// pinky:
pointRightDescription.addCurl(Finger.Pinky, FingerCurl.FullCurl, 1.0);
pointRightDescription.addCurl(Finger.Pinky, FingerCurl.FullCurl, 0.9);
pointRightDescription.addDirection(Finger.Pinky, FingerDirection.HorizontalRight, 1.0);
pointRightDescription.addDirection(Finger.Pinky, FingerDirection.HorizontalRight, 0.9);

export default pointRightDescription;
