import { Finger, FingerCurl, FingerDirection } from '../FingerDescription';
import GestureDescription from '../GestureDescription';


// describe rockSign gesture ðŸ¤˜
const rockSignDescription = new GestureDescription('RockSign');


// thumb:
rockSignDescription.addDirection(Finger.Thumb, FingerDirection.VerticalUp, 1.0);
rockSignDescription.addDirection(Finger.Thumb, FingerDirection.VerticalUp, 0.9);
rockSignDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 1.0);
rockSignDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpRight, 1.0);
rockSignDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 0.9);
rockSignDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpRight, 0.9);

// index:
rockSignDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 1.0);
rockSignDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 0.9);
rockSignDescription.addDirection(Finger.Index, FingerDirection.VerticalUp, 1.0);
rockSignDescription.addDirection(Finger.Index, FingerDirection.VerticalUp, 0.9);
rockSignDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpLeft, 1.0);
rockSignDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpLeft, 0.9);
rockSignDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpRight, 1.0);
rockSignDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpRight, 0.9);

// middle:
rockSignDescription.addCurl(Finger.Middle, FingerCurl.FullCurl, 1.0);
rockSignDescription.addCurl(Finger.Middle, FingerCurl.FullCurl, 0.9);
rockSignDescription.addDirection(Finger.Middle, FingerDirection.VerticalUp, 1.0);
rockSignDescription.addDirection(Finger.Middle, FingerDirection.VerticalUp, 0.9);
rockSignDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpLeft, 1.0);
rockSignDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpLeft, 0.9);
rockSignDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpRight, 1.0);
rockSignDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpRight, 0.9);

// ring:
rockSignDescription.addCurl(Finger.Ring, FingerCurl.FullCurl, 1.0);
rockSignDescription.addCurl(Finger.Ring, FingerCurl.FullCurl, 0.9);
rockSignDescription.addDirection(Finger.Ring, FingerDirection.VerticalUp, 1.0);
rockSignDescription.addDirection(Finger.Ring, FingerDirection.VerticalUp, 0.9);
rockSignDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpLeft, 1.0);
rockSignDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpLeft, 0.9);
rockSignDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpRight, 1.0);
rockSignDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpRight, 0.9);

// pinky:
rockSignDescription.addCurl(Finger.Pinky, FingerCurl.NoCurl, 1.0);
rockSignDescription.addCurl(Finger.Pinky, FingerCurl.NoCurl, 0.9);
rockSignDescription.addDirection(Finger.Pinky, FingerDirection.VerticalUp, 1.0);
rockSignDescription.addDirection(Finger.Pinky, FingerDirection.VerticalUp, 0.9);
rockSignDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpLeft, 1.0);
rockSignDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpLeft, 0.9);
rockSignDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpRight, 1.0);
rockSignDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpRight, 0.9);

export default rockSignDescription;
